const getMin1 = function(a, b) {
    if (a > b) {
        return b
    }
    return a
}

const getMin2 = function(a, b) {
    let min = Infinity
    if (a > b) {
        min = b
    } else {
        min = a
    }
    return min
}

// console.log(getMin1(1, 3));
